  <button type="submit">Submit</button>
</form> 


<ul class="brands">
  <li *ngFor="let brand of brands">
    <p class="name">{{ brand.name }}</p>
    <p class="name">{{ brand.totalProfiles }}</p>
    <p class="name">{{ brand.totalFans }}</p>
    <p class="name">{{ brand.totalEngagement }}</p>

  </li>
</ul>
